:root {
     --color-background: #C8D9E6;
     --color-primary: #2F4156;
     --color-secondary: #567C8Dt;
     --color-TitleBox: #F5EFEB;
     --text-dark: #1f2937;
    /* Adding typography too */
     --text-small: 20px;
     --text-normal: 36px;
     --text-large: 48px;
     --text-huge: 64px;
}
 h1 {
     font-size: var(--text-huge);
}
 h2 {
     font-size: var(--text-large);
}
 h3 {
     font-size: var(--text-normal);
}
p {
     font-size: var(--text-small);
}


